public boolean generatePDFIfNotExists(String primaryKey){
    AppDefinition appDef = AppUtil.getCurrentAppDefinition();

    //String agreementExists = "#form.ags_record.agreement_generated#";

    LogUtil.info("4- Sign Agreement","key is [" + primaryKey + "]");

    if(primaryKey != null && !primaryKey.isEmpty()){
        LogUtil.info("4-Sign Agreement","generate latest agreement after signatory signed");

        PluginManager pluginManager = (PluginManager) AppUtil.getApplicationContext().getBean("pluginManager");
        Plugin plugin = pluginManager.getPlugin("org.joget.marketplace.pluginTemplate.template.PluginTemplateProcessTool");

        WorkflowManager workflowManager = (WorkflowManager) AppUtil.getApplicationContext().getBean("workflowManager");
        WorkflowAssignment assignment;

        //attempt to set mock assignment - works when recordId is activityId
        assignment = workflowManager.getMockAssignment(primaryKey);

        //if assignment is not available, create a new one for the purpose of retrieving primary key
        if(assignment == null){
            assignment = new WorkflowAssignment();
            assignment.setProcessId(primaryKey);
        }

        Map propertiesMap = AppPluginUtil.getDefaultProperties(plugin, null, appDef, null);
        propertiesMap.put("pluginManager", pluginManager);
        propertiesMap.put("appDef", appDef);
        //propertiesMap.put("request", request);
        propertiesMap.put("templateId", "pt-generatePDFRecordID");
        propertiesMap.put("workflowAssignment", assignment);

        ApplicationPlugin generatePDFTool = (ApplicationPlugin) plugin;

        //set properties and execute the tool
        ((PropertyEditable) generatePDFTool).setProperties(propertiesMap);
        generatePDFTool.execute(propertiesMap);

        return true;
    }
    return false;
}

